function onLoad()
    self.createButton({
      label="", click_function="clearPlayArea", function_owner=self,
      position={0,0,0}, height=550, width=550, font_size=0, rotation={0,0,0}
    })
end

function clearPlayArea()

    cardArea = getObjectFromGUID("c04378")
    cards = cardArea.getObjects()
    local discardPosition = {x = 34, y = 5, z = -21}

    for k,card in ipairs(cards) do
        card.setPosition(discardPosition)
    end

end

